package com.example.dmp.controller;

import java.awt.image.BufferedImage;
import java.io.IOException;
import java.net.URI;
import java.net.URISyntaxException;

import com.example.dmp.ClientUtil;
import com.example.dmp.client.DMPClient;
import com.example.dmp.model.User;
import com.example.dmp.repository.UserRepository;
import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;

import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.ResponseBody;
import org.springframework.web.bind.annotation.RestController;

import sun.misc.BASE64Decoder;

/**
 * @version "\$Id$" kenan
 */
@RestController
@RequestMapping("/dmp")
public class DMPController {

    @Autowired
    UserRepository userRepository;

    private static final Log logger = LogFactory.getLog(DMPController.class);

    private static final String POST = "http://localhost:8080/site/integration/dmp/{id}/foo";

    private final String EXAMPLE = "{\"interestedIn\":\"%s\"}";


    @GetMapping(value = "/{id}/change/{interest}")
    public ResponseEntity change(@PathVariable("id") String id, @PathVariable("interest") String interest) throws URISyntaxException {
        ObjectMapper mapper = new ObjectMapper();
        final DMPClient dmpClient = ClientUtil.createDMPClient("http://localhost:8080/site");
        JsonNode node = null;
        try {
            node = mapper.readTree(String.format(EXAMPLE, interest));
            final User userByBreId = userRepository.findUserByBreId(id);
            userByBreId.setInterest(interest);
            userRepository.save(userByBreId);
            dmpClient.post(id, "foo", node);
        } catch (Exception e) {
            logger.error("error...", e);
        }
        HttpHeaders headers = new HttpHeaders();
        headers.setLocation(URI.create("http://localhost:9090/home"));
        return new ResponseEntity(headers, HttpStatus.FOUND);
    }

    @GetMapping(value = "/{partner}/pixel.png", produces = MediaType.IMAGE_PNG_VALUE)
    public
    @ResponseBody
    byte[] getPixel(@PathVariable("partner") String partner, @RequestParam(value = "uuid", required = false) String id, @RequestParam(value = "ca_cookie", required = false) String ourId) {
        logger.info("partner: " + partner);
        logger.info("uuid: " + id);
        logger.info("dmp id: " + ourId);
        final User userByBreId = userRepository.findUserByBreId(id);
        if (userByBreId == null) {
            User user = new User();
            user.setBreId(id);
            user.setDmpId(ourId);
            ObjectMapper mapper = new ObjectMapper();
            JsonNode node = null;
            try {
                node = mapper.readTree(String.format(EXAMPLE, "nothing yet"));
            } catch (Exception e) {
                logger.error("error", e);
            }
            user.setInterest("nothing yet");
            userRepository.save(user);
            final DMPClient dmpClient = ClientUtil.createDMPClient("http://localhost:8080/site");
            dmpClient.post(id, "foo", node);
        }
        try {
            String base64Img = "";
            BufferedImage image = null;
            byte[] imageByte;
            BASE64Decoder decoder = new BASE64Decoder();
            imageByte = decoder.decodeBuffer(base64Img);
            return imageByte;
        } catch (IOException e) {
            logger.error(e);
        }
        return null;
    }


}


